mapper.select:
select OC_IMPORT_BATCH.IMPORT_BATCH as IMPORT_BATCH, OC_IMPORT_BATCH.IMPORT_PERSON_ID as IMPORT_PERSON_ID, OC_IMPORT_BATCH.IMPORT_TIME as IMPORT_TIME, OC_IMPORT_BATCH.TASK_TYPE as TASK_TYPE, OC_IMPORT_BATCH.BATCH_FLAG as BATCH_FLAG, ucc_users.nick_name as IMPORT_PERSON_NAME from OC_IMPORT_BATCH as OC_IMPORT_BATCH left join ucc_users as ucc_users on ucc_users.user_id = OC_IMPORT_BATCH.IMPORT_PERSON_ID where OC_IMPORT_BATCH.BATCH_FLAG = #{batchFlag,jdbcType=VARCHAR} and OC_IMPORT_BATCH.IMPORT_PERSON_ID = #{importPersonId,jdbcType=VARCHAR}
or OC_IMPORT_BATCH.IMPORT_BATCH in ( ${importBatchs} )
order by OC_IMPORT_BATCH.IMPORT_TIME DESC

mapper.insert:
insert into OC_IMPORT_BATCH (IMPORT_BATCH, IMPORT_PERSON_ID, IMPORT_TIME, TASK_TYPE, BATCH_FLAG 
  )
values (#{importBatch,jdbcType=VARCHAR}, #{importPersonId,jdbcType=VARCHAR}, #{importTime,jdbcType=VARCHAR},  
#{taskType,jdbcType=VARCHAR}, #{batchFlag,jdbcType=VARCHAR} 
  )

mapper.update:
update OC_IMPORT_BATCH
set BATCH_FLAG = #{batchFlag,jdbcType=VARCHAR} 
where IMPORT_BATCH in ( ${importBarchs} )

mapper.select:
select CONCAT(OC_IMPORT_BATCH.IMPORT_BATCH,concat(",",concat(OC_IMPORT_BATCH.IMPORT_BATCH,concat(" ",concat(OC_IMPORT_BATCH.TASK_TYPE,concat(" ",ucc_users.nick_name)))))) from OC_IMPORT_BATCH as OC_IMPORT_BATCH left join ucc_users as ucc_users on ucc_users.user_id = OC_IMPORT_BATCH.IMPORT_PERSON_ID where 1=1
and (OC_IMPORT_BATCH.IMPORT_BATCH like '%${word}%' or OC_IMPORT_BATCH.TASK_TYPE like '%${word}%' or ucc_users.nick_name like '%${word}%')
and OC_IMPORT_BATCH.BATCH_FLAG = #{batchFlag}
and OC_IMPORT_BATCH.IMPORT_PERSON_ID = #{userId}
order by OC_IMPORT_BATCH.IMPORT_TIME DESC
