mapper.select:
SELECT TASK_DETAIL_ID, BUSINESS_CODE, TASK_TYPE_ID, DEPT_MESH_ID, DEPT_AREA_ID, DEVELOPMENT_DEPT, TASK_CODE, p1.dept_name DEPT_MESH_NAME, p2.dept_name DEPT_AREA_NAME, p3.dept_name DEVELOPMENT_DEPT_NAME, TASK_STATUS FROM OC_TASK_DETAIL d LEFT JOIN ucc_depts p1 ON d.DEPT_MESH_ID = p1.dept_id LEFT JOIN ucc_depts p2 ON d.DEPT_AREA_ID = p2.dept_id LEFT JOIN ucc_depts p3 ON d.DEVELOPMENT_DEPT = p3.dept_id WHERE 1 = 1
and d.IMPORT_BATCH = #{importBatch}
order by d.TASK_CODE DESC
limit #{begin},#{showLines}

SELECT TASK_DETAIL_ID FROM OC_TASK_DETAIL d LEFT JOIN ucc_depts p1 ON d.DEPT_MESH_ID = p1.dept_id LEFT JOIN ucc_depts p2 ON d.DEPT_AREA_ID = p2.dept_id LEFT JOIN ucc_depts p3 ON d.DEVELOPMENT_DEPT = p3.dept_id WHERE 1 = 1
and d.IMPORT_BATCH = #{importBatch}
order by d.TASK_CODE DESC

SELECT d.TASK_DETAIL_ID, d.BUSINESS_CODE, d.TASK_TYPE_ID, d.DEPT_MESH_ID, d.DEPT_AREA_ID, d.DEVELOPMENT_DEPT, d.TASK_CODE, c.cust_name AS USER_NAME, d.DEFULT_PHONE, d.TASK_STATUS FROM OC_TASK_DETAIL d LEFT JOIN ucc_customers c ON d.BUSINESS_CODE = c.businesscode WHERE 1 = 1
order by d.TASK_CODE DESC
limit #{begin},#{showLines}

mapper.update:
update OC_TASK_DETAIL

  TASK_STATUS=#{status},
  END_DATE=#{endDate},
  ROPERATE_DEPT_ID=#{roperateDeptId}

where TASK_DETAIL_ID=#{taskDetailId}
