mapper.select:
select t.TASK_TRANSFER_ID as TASK_TRANSFER_ID, t.TASK_DETAIL_ID as TASK_DETAIL_ID, t.TRANSFER_STATUS as TRANSFER_STATUS, t.ROPERATE_PERSON_ID as ROPERATE_PERSON_ID, t.ROPERATE_DEPT_ID as ROPERATE_DEPT_ID, t.CALL_MEMO as CALL_MEMO, t.CALL_RESULT as CALL_RESULT, t.TRANSFE_ROPERATE as TRANSFE_ROPERATE, t.TRANSFER_TIME as TRANSFER_TIME, t.CDR_ID as CDR_ID, t.BACK_MEMO as BACK_MEMO, t.OPERATOR_ID as OPERATOR_ID, t.OPERATOR_DEPT as OPERATOR_DEPT, c.destination_number as destination_number, c.call_time as call_time, d.TASK_CODE as TASK_CODE, ud.dept_name as RECEIVE_DEPT_NAME, uu.user_name as user_name from OC_TASK_TRANSFER t left join pbx_cdrs c on t.CDR_ID = c.cdr_id left join OC_TASK_DETAIL d on t.TASK_DETAIL_ID = d.TASK_DETAIL_ID left join ucc_depts as ud on t.ROPERATE_DEPT_ID = ud.dept_id left join ucc_users as uu on t.ROPERATE_PERSON_ID = uu.user_id where t.TASK_DETAIL_ID in (
#{item.taskDetailId}
) and t.CDR_ID is not null order by t.TASK_DETAIL_ID, t.TRANSFER_TIME DESC

