mapper.select:
select
a.*,
b.domain_name as domain_name
from ucc_customers as a
LEFT join ucc_domain as b ON a.domain_id = b.domain_id
where 1=1

  AND a.type = #{type,jdbcType=INTEGER}


  AND a.cust_id = #{custId,jdbcType=VARCHAR}


  AND a.domain_id = #{domainId,jdbcType=VARCHAR}


  AND a.cust_name LIKE '%${custName}%'


  and DATE_FORMAT(a.updated_time,'%Y-%m-%d %H:%i:%S') >=#{startTime}


  and DATE_FORMAT(a.updated_time,'%Y-%m-%d %H:%i:%S') <=#{endTime}

SELECT count(DISTINCT concat(c.cust_id, tt.TASK_TYPE_ID)) FROM ucc_customers c INNER JOIN OC_TASK_DETAIL d ON c.businesscode = d.BUSINESS_CODE AND d.domain_id = c.domain_id inner join OC_TASK_TRANSFER t on t.TASK_DETAIL_ID = d.TASK_DETAIL_ID and t.TRANSFER_STATUS = d.TASK_STATUS LEFT JOIN ucc_depts uc ON uc.dept_id = d.ROPERATE_DEPT_ID INNER JOIN OC_TASK_TYPE tt ON d.TASK_TYPE_ID = tt.TASK_TYPE_ID WHERE 1=1 and d.TASK_STATUS IN ('2', '3')
and d.ROPERATE_DEPT_ID = #{roperateDeptId}
and t.OPERATOR_ID = #{operatorId}
and d.ROPERATE_DEPT_ID in (${deptIdAndChildIds})
and d.TASK_TYPE_ID = #{taskTypeId}

SELECT DISTINCT tt.TASK_TYPE_ID as TASK_TYPE_ID, tt.TASK_TYPE_NAME as TASK_TYPE_NAME, c.businesscode businesscode, c.cust_name cust_name, c.STATUS STATUS, c.installedaddress installedaddress, c.ponlogo ponlogo, c.tariffname tariffname, c.DEFULT_PHONE DEFULT_PHONE, c.cust_id, uc.dept_name as dept_name FROM ucc_customers c INNER JOIN OC_TASK_DETAIL d ON c.businesscode = d.BUSINESS_CODE AND d.domain_id = c.domain_id inner join OC_TASK_TRANSFER t on t.TASK_DETAIL_ID = d.TASK_DETAIL_ID and t.TRANSFER_STATUS = d.TASK_STATUS LEFT JOIN ucc_depts uc ON uc.dept_id = d.ROPERATE_DEPT_ID INNER JOIN OC_TASK_TYPE tt ON d.TASK_TYPE_ID = tt.TASK_TYPE_ID WHERE 1 = 1 AND d.TASK_STATUS IN ('2', '3')
and d.ROPERATE_DEPT_ID = #{roperateDeptId}
and t.OPERATOR_ID = #{operatorId}
and d.ROPERATE_DEPT_ID in (${deptIdAndChildIds})
and d.TASK_TYPE_ID = #{taskTypeId}
GROUP BY c.businesscode
limit #{begin},#{showLines}

mapper.update:
update ucc_customers
set DEFULT_PHONE = #{defultPhone,jdbcType=VARCHAR}
where businesscode = #{businesscode,jdbcType=VARCHAR} and  domain_id = #{domainId,jdbcType=VARCHAR}

update ucc_customers
set

  changephone1 = #{changephone1,jdbcType=VARCHAR}


  changephone2 = #{changephone2,jdbcType=VARCHAR}


  changephone3 = #{changephone3,jdbcType=VARCHAR}


  , DEFULT_PHONE = #{defultPhone,jdbcType=VARCHAR}

where businesscode = #{businesscode,jdbcType=VARCHAR} and domain_id = #{domainId,jdbcType=VARCHAR}

mapper.select:
SELECT a.PULL_BLACK_REASON pullBlackReason
  FROM ucc_customers a
WHERE a.type = 7
  AND a.businesscode = #{businesscode,jdbcType=VARCHAR}
  AND a.domain_id = #{domainId,jdbcType=VARCHAR}

select
c.businesscode businesscode
from ucc_customers c
inner join OC_TASK_DETAIL d on c.businesscode = d.BUSINESS_CODE and c.domain_id = d.domain_id
inner join OC_TASK_TRANSFER t on t.TASK_DETAIL_ID = d.TASK_DETAIL_ID and t.TRANSFER_STATUS = d.TASK_STATUS


    and d.ROPERATE_DEPT_ID = #{roperateDeptId}


    and t.TRANSFER_STATUS = #{taskStatus}


    and d.TASK_TYPE_ID = #{taskTypeId}



    and d.BUSINESS_CODE != #{businesscode}



  limit #{showLines}

select

from ucc_customers as uc
where uc.businesscode = #{businessCode}
and uc.domain_id = #{domainId}
