mapper.select:
select
a.*,
b.domain_name as domain_name
from ucc_customers as a
LEFT join ucc_domain as b ON a.domain_id = b.domain_id
where 1=1

  AND a.type = #{type,jdbcType=INTEGER}


  AND a.cust_id = #{custId,jdbcType=VARCHAR}


  AND a.domain_id = #{domainId,jdbcType=VARCHAR}


  AND a.cust_name LIKE '%${custName}%'


  and DATE_FORMAT(a.updated_time,'%Y-%m-%d %H:%i:%S') >=#{startTime}


  and DATE_FORMAT(a.updated_time,'%Y-%m-%d %H:%i:%S') <=#{endTime}

select

from ucc_customers where businesscode in ( ${businessCode}
)
