mapper.select:
select

from ucc_notice
where 1=1
and notice_type =  #{noticeType,jdbcType=VARCHAR}
and notice_id = #{noticeId,jdbcType=VARCHAR}

  SELECT
  *
  FROM
      ucc_notice 
  WHERE 1=1

  
      AND notice_title LIKE '%${noticeTitle}%'
  
  
      AND notice_type = #{noticeType,jdbcType=VARCHAR}
  
  
      AND Date(start_date) >= #{noticeTimeFrom,jdbcType=DATE}
  
  
      AND Date(start_date) <= #{noticeTimeTo,jdbcType=DATE}
     
  
      AND user_id= #{userId,jdbcType=VARCHAR}
